function distributeColumnWidths(table){
    if(table.nodeName !== 'TABLE')return;
    //We're just going to add a percentage each of the tds to make them all the same width.
    //Get the rows
    var rows = table.getElementsByTagName('tr');

    //Traverse the rows
    for(var row_index = 0; row_index < rows.length; row_index ++){
        var row = rows[row_index];

        //Get the cells
        var cells = row.getElementsByTagName('td');

        //Cell width is 100% divided by the number of cells, rounded down
        var num_cells = cells.length;
        var cell_width = Math.floor(100 / num_cells) + '%';

        //Traverse the cells, adding the percentage to each of the cells.
        for(var cell_index = 0; cell_index < num_cells; cell_index ++){
            cells[cell_index].setAttribute('width', cell_width);
        };
    };
}



