(function($){
    $.fn.citetabs = function(){
        return this.each(function(){
            var $this = $(this);
            function setup() {
                var $tabset = $this.find('ul');
                if (!$tabset.hasClass('nav')){
                    $tabset.addClass('nav').addClass('nav-tabs');
                };
                var $tabs  = $this.find('ul li');
                var $panes = $this.find('div.tab-content > div');
                $panes.hide();
                $tabs.each(function(i){
                    var $tab    = $(this).wrapInner("<a>");
                    var $pane   = $panes.eq(i);
                    var pane_id = $pane.attr('id') || utils.randomId(10, 'pane-');
                    $pane.attr('id', pane_id);
                    $tab.find('a').attr('href', '#'+pane_id);
                    $tab.click(function (e) {
                        e.preventDefault();

                        var $pane_to_activate = $($tab.find('a').attr('href'));
                        if (!$pane_to_activate.is(":visible")){
                            $panes.hide();
                            $pane_to_activate.fadeIn();
                        }
                        $tabs.removeClass('active');
                        $tab.addClass('active');
                    });
                });
            }
            function init(){
                var $tabs = $this.find('ul li');
                var $active = $tabs.filter('.active');
                if ($active.length == 0) {
                    $tabs.eq(0).click();
                } else {
                    $active.eq(0).click();
                }
            }
            setup();
            init();
        });
    };
})(jQuery);

