(function( $ ){
    $.fn.citetoggle = function(){
        return this.each(function(){
            var $this = $(this);
            var $trigger = $this.find('.toggle-trigger');
            var $content = $this.find('.toggle-content');
            var $icon = $('<span>').prependTo($trigger);
            var minus_icon = '<i class="fa fa-caret-down"></i> ';
            var plus_icon = '<i class="fa fa-caret-right"></i> ';
            function init() {
                $content.hide();
                $icon.html(plus_icon);
            }
            $trigger.click(function(){
                $content.slideToggle('slow');
                $trigger.toggleClass('toggle-trigger-open');
                if ($trigger.hasClass('toggle-trigger-open')) {$icon.html(minus_icon);}
                else {$icon.html(plus_icon);}
            });
            init();
        });
    };
})(jQuery);

